mapper.select:
select io.id incomeOrderId, io.type, um.name userName, io.total_money totalMoney, io.unpaid_money unpaidMoney, io.day, io.status
from income_order io
left join user_manage um on io.user_id = um.id
where io.deleted = 0 and io.day between #{startTime} and #{endTime}

        and io.type = #{param.orderType}


        and io.status = #{param.status}


        and um.name like concat('%', #{param.name}, '%')
